/**
 * Git commit message style
 * $kz commit
 */
const commit = require('./commit')

module.exports = function(program, cleanArgs, chalk) {
  program
    .command('commit')
    .description('The standard message for Git commit')
    .option('--init', 'Initialize to set up kz commit ')
    // .option('-f,--force', 'force')
    .action((cmd) => {
      commit(cleanArgs(cmd), chalk)
    })
}
